mapper.delete:
delete from cart
    where cartNum = #{cartNum}
        and userId = #{userId}

mapper.select:
select ifnull(sum(pdPrice * cartStock), 0) money
from cart c, product p
where c.pdNum = p.pdNum and c.userId = #{userId}

select orderId, userId, rentCar, orderPhon, amount, orderDate,orderName,reQu
	from boo_order
		where userId = #{userId}
